from genecomb.genecomb import GeneComb
from genecomb.genecomb import read_fasta
import pytest

@pytest.mark.parametrize(
    "input_file, header, seq",
    [
        (
            "genecomb/tests/fasta_files/homo_seq.fasta", 
            ">PA369630.1 JP 2021531802-A/1: ANTIBODY BINDING TO HUMAN HER2 AND PREPARATION METHOD AND USE THEREOF",
            ""
        ),
        (
            "genecomb/tests/fasta_files/single_seq_collagen.fasta",
            ">AH002560.3 Gallus gallus collagen a2 mRNA gene, partial sequence; and precollagen alpha-2, collagen alpha-2, collagen alpha 2, collagen a2, and collagen alpha-2 genes, partial cds",
            ""
        ),
        (
            "genecomb/tests/fasta_files/single_seq_foxo3.fasta",
            ">KY421028.1 Andrias davidianus foxo3 mRNA, complete cds",
            ""
        ),
    ]
)
def test_fasta_input_single(input_file, header, seq):
    genecomb = read_fasta(input_file)
    assert genecomb.header == header
    assert genecomb.seq == seq


@pytest.mark.parametrize(
"input_file, header, seq",
    [
        (
            "genecomb/tests/fasta_files/empty_seq.fasta",
            '>',
            ''
        ),
        (
            "genecomb/tests/fasta_files/empty_file.fasta",
            '',
            ''
        )

    ]
)
def test_fasta_input_empty(input_file, header, seq):
    genecomb = read_fasta(input_file)
    assert genecomb.header == header
    assert genecomb.seq == seq



@pytest.mark.parametrize(
    "input_file, seqs",
    [
        (
            "genecomb/tests/fasta_files/all_seqs.fasta",
            [
                {
                    'header': '>PA369630.1 JP 2021531802-A/1: ANTIBODY BINDING TO HUMAN HER2 AND PREPARATION METHOD AND USE THEREOF',
                    'seq': ''
                },
                {
                    'header': '>KY421028.1 Andrias davidianus foxo3 mRNA, complete cds',
                    'seq': ''
                },

            ]
        
        )
       
    ]
)
def test_multiple_seqs(input_file, seqs):
    genecomb = read_fasta(input_file)
    for (input, expected) in zip(genecomb, seqs):
        assert input.header == expected['header']
        assert input.seq == expected['seq']
